mapper.select:
  
    SELECT t.id, t.uid, t.username, t.alww, t.type, t.status, t.cardid, t.createtime, t.remark FROM t_tgks_cgms_cardorder t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.UID = #{uid}
    
    
        AND t.USERNAME = #{username}
    
    
        AND t.ALWW = #{alww}
    
    
        AND t.TYPE = #{type}
    
    
        AND t.STATUS = #{status}
    
    
        AND t.CARDID = #{cardId}
    
    
        AND t.CREATETIME = #{createTime}
    
    
        AND t.REMARK = #{remark}
    


    ORDER BY ${sortSql}
